package pk02;

public class AssignTest_01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int aVar=20;
		System.out.println("aVar ==10 :"+(aVar+=10)); //aVar = aVar+10;
		System.out.println("aVar -=10 :"+(aVar-=10)); //aVar = aVar-10;
		System.out.println("aVar *=10 :"+(aVar*=10)); //aVar = aVar*10;
		System.out.println("aVar /=10 :"+(aVar/=10)); //aVar = aVar/10;
		System.out.println("aVar %=10 :"+(aVar%=10)); //aVar = aVar%10;


	}

}
