package pk02;

public class AssignTest_02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num1=10;
		int num2=20;
		int num3=2;
		
		num1 += num2; //계속 숫자가 누적
		System.out.println(num1);
		
		num1 -= num2;
		System.out.println(num1);
		
		num1 *= num3;
		System.out.println(num1);
		
		num1 %= num3;
		System.out.println(num1);
		
		

	}

}
