package pk02;

public class RelaTest_01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char a,b;
		a='A'; //65 아스키코드
		b='B'; //66
		
		System.out.println("a>b : " + (a>b));
		//여기서 false는 0을뜻 false == 0
		System.out.println("a>b : " + (a<b));
		System.out.println("a>b : " + (a==b));
		System.out.println("a>b : " + (a!=b));
		

	}

}
